<?php/** * Created by PhpStorm. * User: khaoula * Date: 1/14/2023 * Time: 8:46 PM */namespace App\Form;use App\Document\contact;use Symfony\Component\Form\AbstractType;use Symfony\Component\Form\Extension\Core\Type\ChoiceType;use Symfony\Component\Form\Extension\Core\Type\NumberType;use Symfony\Component\Form\Extension\Core\Type\TelType;use Symfony\Component\Form\Extension\Core\Type\TextareaType;use Symfony\Component\Form\FormBuilderInterface;use Symfony\Component\OptionsResolver\OptionsResolver;class ContactType extends AbstractType{    /**     * {@inheritdoc}     */    public function buildForm(FormBuilderInterface $builder, array $options)    {        $builder            ->add('Address',TextareaType::class)            ->add('City',ChoiceType::class,['choices'=>[                'Bizerte'=>'Bizerte',                'Tunis'=>'Tunis',                'Jerba'=>'Jerba'            ]])            ->add('zipcode',NumberType::class)            ->add('phone',TelType::class)        ;    }    public function configureOptions(OptionsResolver $resolver): void    {        $resolver->setDefaults([            'data_class' => contact::class,        ]);    }}