<?php/** * Created by PhpStorm. * User: khaoula * Date: 1/14/2023 * Time: 8:46 PM */namespace App\Form;use App\Document\Pet;use Symfony\Component\Form\AbstractType;use Symfony\Component\Form\Extension\Core\Type\CheckboxType;use Symfony\Component\Form\Extension\Core\Type\ChoiceType;use Symfony\Component\Form\Extension\Core\Type\DateType;use Symfony\Component\Form\Extension\Core\Type\TextareaType;use Symfony\Component\Form\Extension\Core\Type\TextType;use Symfony\Component\Form\FormBuilderInterface;use Symfony\Component\OptionsResolver\OptionsResolver;class PetType extends AbstractType{    /**     * {@inheritdoc}     */    public function buildForm(FormBuilderInterface $builder, array $options)    {        $builder            ->add('pseudo', TextType::class)            ->add('gender',ChoiceType::class,['choices'=>[                'Male'=>'Male',                'Female'=>'Female'            ]])            ->add('race', TextType::class)            ->add('color', TextType::class)            ->add('birthdate',DateType::class, [                'widget' => 'single_text',                'attr' => ['class' => 'js-datepicker']            ])            ->add('vaccinated',CheckboxType::class)            ->add('description',TextType::class)        ;    }    public function configureOptions(OptionsResolver $resolver): void    {        $resolver->setDefaults([            'data_class' => Pet::class,        ]);    }}