<?phpnamespace App\Form;use App\Document\PetParent;use Symfony\Component\Form\AbstractType;use Symfony\Component\Form\Extension\Core\Type\EmailType;use Symfony\Component\Form\Extension\Core\Type\PasswordType;use Symfony\Component\Form\FormBuilderInterface;use Symfony\Component\OptionsResolver\OptionsResolver;class UserType extends AbstractType{    /**     * {@inheritdoc}     */    public function buildForm(FormBuilderInterface $builder, array $options)    {        $builder            ->add('name')            ->add('email',EmailType::class)            ->add('password',PasswordType::class)        ;    }    public function configureOptions(OptionsResolver $resolver): void    {        $resolver->setDefaults([            'data_class' => PetParent::class,        ]);    }}