<?php/** * Created by PhpStorm. * User: khaoula * Date: 2/12/2023 * Time: 12:40 PM */namespace App\MessageHandler;use App\Message\ContactMessage;use Symfony\Component\Mailer\Exception\TransportExceptionInterface;use Symfony\Component\Mailer\MailerInterface;use Symfony\Component\Messenger\Handler\MessageHandlerInterface;use Symfony\Component\Mime\Message;class ContactMessageHandler implements MessageHandlerInterface{    private $mailer;    public function __construct(MailerInterface $mailer)    {        $this->mailer = $mailer;    }    public function __invoke(ContactMessage $message)    {        try {            $this->mailer->send(                (new Message('[Demo] Notification'))                    ->setFrom($message->getEmail())                    ->setTo('kaoulatouati@gmail.com')                    ->setBody('<h1>Notification</h1><p>' . $message->getMessage() . '</p>',                        'text/html'                    )            );        } catch (TransportExceptionInterface $e) {        }    }}